import Header from "./components/Header";
import { useState } from "react";

function App() {
  const [darkMode, setDarkMode] = useState(true);

  function toggleDarkmode(){
    setDarkMode(!darkMode)
  }

  return (
    <div className="container">
        <Header 
          darkMode={darkMode}
        />
    </div>
  );
}

export default App;
