import Header from "./components/Header";
import { useState } from "react";

function App() {
  const [darkMode, setDarkMode] = useState(false);

  function toggleDarkmode(){
    setDarkMode(!darkMode)
  }

  return (
    <div className="container">
        <Header 
          darkMode={darkMode}
          toggleDarkmode={toggleDarkmode}
        />
    </div>
  );
}

export default App;
